import { createContext, useContext, useState } from "react";
import movieData from '../json/movies.json'
const MovieContext = createContext();
const ChosenContext = createContext();
export const MovieProvider = (props) =>{
    const {children} = props;
    const [getData,setMovieData] = useState(movieData);
    const [chosenMovie, setChosenMovie] = useState();
    return (<><MovieContext.Provider value={{getData,setMovieData, }}>
        {children}
    </MovieContext.Provider>
    <ChosenContext.Provider value={{chosenMovie,setChosenMovie}}>
        {children}
    </ChosenContext.Provider>
    </>
    )
}

export const UseMovieContext = () => useContext(MovieContext);
export const UseChosenMovieContext = () => useContext(chose)
